class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<pair<int,int>>>v(n);
        for(int qi = 0;qi<queries.size();++qi)
            {
                auto q = queries[qi];
                int l = q[0] , r = q[1] , val = q[2];
                for(int i=l;i<=r;++i)
                    {
                        v[i].emplace_back(val,qi);
                    }
            }
        int res = 0;

        for(int i = 0;i<n;++i)
            {
                if(nums[i]==0) continue;
                set<int>s;
                s.emplace(0);
                bool flag = false;
                unordered_set<int>tmp;
                for(auto &p:v[i])
                    {
                        for(const int &n:s)
                            {
                                if(n+p.first==nums[i])
                                {
                                    res = max(res,p.second+1);
                                    flag = true;
                                    break;
                                }
                                else if(n+p.first<nums[i])
                                {
                                    tmp.emplace(n+p.first);
                                }
                            }
                        for(const int &n:tmp)
                            {
                                s.emplace(n);
                            }
                        if(flag)break;
                    }
                if(!flag)
                {
                    return -1;
                }
            }
        return res;
    }
};

