class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n=nums.length,count_zero=0;
        HashSet<Integer>[] sarray = new HashSet[n];
        for(int i=0;i<n;++i){
            sarray[i] = new HashSet<>();
            sarray[i].add(nums[i]);
            if(nums[i]==0)++count_zero;
        }
        if(count_zero==n)return 0;
        for(int q=0;q<queries.length;++q){
            for(int i=queries[q][0];i<=queries[q][1];++i){
                if(!sarray[i].contains(0)){
                    if(sarray[i].contains(queries[q][2])){
                        sarray[i].add(0);
                        ++count_zero;
                        if(count_zero==n)return q+1;
                    }else{
                        List<Integer> l = new ArrayList<>(sarray[i]);
                        for(Integer j:l){
                            if(j-queries[q][2]>0)sarray[i].add(j-queries[q][2]);
                        }
                    }
                }
            }
        }
        return -1;
    }
}

