func minZeroArray(nums []int, queries [][]int) int {
    n,m := len(nums),len(queries)

    ans := 0

    need := make([]int,n)
    for i:=0; i<n; i++ {
        need[i] = m+1
        num := nums[i]

        f := make([][]bool,m+1)
        for j:=0; j<=m; j++ {
            f[j] = make([]bool,num+1)
        }
        f[0][0] = true

        if num != 0 {
            for j:=1; j<=m; j++ {
                l,r,val := queries[j-1][0],queries[j-1][1],queries[j-1][2]
                for k:=0; k<=num; k++ {
                    f[j][k] = f[j][k] || f[j-1][k]
                    if l<=i && i<=r && k>=val {
                        f[j][k] = f[j][k] || f[j-1][k-val]
                    }
                }
                if f[j][num] {
                    need[i] = j
                    break
                }
            }   
        } else {
            need[i] = 0
        }

        // fmt.Println(num,need[i])

        if need[i] > m {
            return -1
        } else {
            ans = max(ans,need[i])
        }
    }

    return ans
}

