class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = queries.length;
        int left = 0, right = n + 1;
        while (left < right) {
            int mid = (right - left) / 2 + left;
            if (check(nums, queries, mid)) right = mid;
            else left = mid + 1;
        }
        return right > n ? -1 : right;
    }

    public boolean check(int[] nums, int[][] queries, int k) {
        int n = nums.length;
        List<Integer>[] add = new ArrayList[n];
        List<Integer>[] sub = new ArrayList[n];
        Arrays.setAll(add, i -> new ArrayList<>());
        Arrays.setAll(sub, i -> new ArrayList<>());
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1];
            add[l].add(queries[i][2]);
            if (r + 1 < n) sub[r + 1].add(queries[i][2]);
        }
        Map<Integer, Integer> cnt = new HashMap<>();
        for (int i = 0; i < n; i++) {
            for (Integer x : add[i]) {
                cnt.merge(x, 1, Integer::sum);
            }
            for (Integer x : sub[i]) {
                cnt.merge(x, -1, Integer::sum);
            }
            boolean[] dp = new boolean[1001];
            dp[0] = true;
            for (Map.Entry<Integer, Integer> e : cnt.entrySet()) {
                int x = e.getKey(), c = e.getValue();
                for (int j = 0; j < c; j++) {
                    for (int v = 1000; v >= 0; v--) {
                        if (v + x <= 1000) dp[v + x] |= dp[v];
                    }
                }
            }
            if (!dp[nums[i]]) return false;
        }
        return true;
    }
}

