            memset(dp, -1, sizeof dp);
            dp[0] = 0;
            for(int i = 1; i <= cnt; i++) {
                // cout<<"from";
                // for(int o = 0; o <= x; o++) {
                //     printf("(%d,%d) ",o,dp[o]);
                // }cout<<endl;
                for(int j = x; j >= tmp[i]; j--) {
                    if(dp[j - tmp[i]] == 0) {
                        dp[j] = 0;
                    }
                }
                // cout<<"to";for(int o = 0; o <= x; o++) {
                //     printf("(%d,%d) ",o,dp[o]);
                // }cout<<endl;
            }
            // for(int i = 1; i <= cnt; i++) {
            //     cout << tmp[i] << " ";
            // }cout<<endl;

            // cout << endl;
            if(dp[x] == -1) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size(), ans = -1;
        // l = r;
        while(l <= r) {
            int mid = l + r >> 1;
            if(check(nums, queries, mid)) {
                r = mid - 1;
                ans = mid;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

