class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size();
        int f = 1;
        for (int x : a) {
            if (x != 0) {
                f = 0;
            }
        }
        if (f) return 0;

        vector<bitset<1010>> dp(n);
        for (int i = 0; i < n; i ++) {
            dp[i].reset();
            dp[i][0] = true;
        }

        int m = q.size();
        for (int i = 0; i < m; i ++) {
            int l = q[i][0], r = q[i][1], val = q[i][2];
            for (int j = l; j <= r; j ++) {
                dp[j] |= (dp[j] << val);
            }
            int f = 1;
            for (int j = 0; j < n; j ++) {
                // cout << dp[j].test(a[j]) << ' ';
                if (!dp[j].test(a[j])) {
                    f = 0;
                    break;
                }
            }
            if (f) return i + 1;
        }

        return -1;
    }
};

