    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<bool> > dp(n, vector<bool>(1050));
        for(int i=0; i<n; i++)
            dp[i][0]=true;

        bool allZero = true;
        for(int ele: nums)
            allZero = allZero &&(ele==0);
