class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        res = []
        for i in range(n):
            if nums[i] == 0:
                res.append(0)
                continue

            cover_q = []
            for ind,(l,r,val) in enumerate(queries):
                if l<= i <= r:
                    cover_q.append((ind,val))

            dp = {0:-1}
            for ind,val in cover_q:
                dp_new = {}
                for s in dp.keys():
                    current = dp[s]
                    s_new = s+ val
                    if s_new > nums[i]:
                        continue
                    new_c = max(current,ind)
                    if (s_new not in dp_new) or new_c < dp_new[s_new]:
                        dp_new[s_new] = new_c

                for s in dp_new.keys():
                    if (s not in dp) or dp_new[s] < dp.get(s,float('inf')):
                        dp[s] = dp_new[s]

            if nums[i] not in dp:
                return -1
            res.append(dp[nums[i]] + 1)
        return max(res)

