impl Solution {
    pub fn min_zero_array(nums: Vec<i32>, queries: Vec<Vec<i32>>) -> i32 {
        if nums.iter().any(|&x| x < 0) {
            return -1;
        }

        let n = nums.len();

        let mut elem_ops = vec![vec![]; n];

        for (j, q) in queries.iter().enumerate() {
            let l = q[0] as usize;
            let r = q[1] as usize;
            let val = q[2];
            for i in l..=r {
                elem_ops[i].push((j, val));
            }
        }

        let mut max_k = -1;

        for (i, &target) in nums.iter().enumerate() {
            if target == 0 {
                continue;
            }

            let mut dp = std::collections::HashMap::new();
            dp.insert(0, -1);
            let mut min_k = i32::MAX;

            for &(j, val) in &elem_ops[i] {
                let current_j = j as i32;
                let current_val = val;

                // 
                let keys: Vec<_> = dp.keys().cloned().collect();

                for sum in keys {
                    let new_sum = sum + current_val;
                    let new_max = dp[&sum].max(current_j);

                    // 
                    if new_sum == target {
                        min_k = min_k.min(new_max);
                    }

                    dp.entry(new_sum).and_modify(|e| *e = (*e).min(new_max)).or_insert(new_max);
                }
            }

            if !dp.contains_key(&target) {
                return -1;
            }

            max_k = max_k.max(min_k);
        }

        if max_k == -1 {
            0
        } else {
            max_k + 1
        }
    }
}

