class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        int mx = 0;
        for(auto i: nums) {
            mx = max(mx, i);
        }
        if(mx == 0) return 0;
        vector<vector<int>> g(n+5);
        vector<int> sum(n+5, 0);
        auto check = [&](int x) -> bool{
            for(int i = 0; i < n; i++) {
                if(sum[i] < nums[i]) return 0;
                vector<bool> v(nums[i]+5, 0);
                v[0] = 1;
                for(auto j: g[i]) {
                    for(int k = nums[i]; k >= 0; k--) {
                        if(k >= j) v[k] = v[k - j] | v[k];
                    }
                }
                if(!v[nums[i]]) return 0;
            }
            return 1;
        };
        for(int i = 0; i < q.size(); i++) {
            int l = q[i][0], r = q[i][1], val = q[i][2];
            for(int j = l; j <= r; j++) {
                g[j].push_back(val);
                sum[j] += val;
            }
            if(check(i)) {
                return i + 1;
            } 
        }
        return -1;
    }
};

