class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int tmp=-1;
        for(int i=0;i<nums.size();i++){
            auto s=visit(queries,i,nums[i]);
            if(s<0){
                return -1;
            }else{
                tmp=max(tmp,s);
            }
        }
        return tmp;
    }

    int visit(vector<vector<int>>& queries,int p,int v){
        if(v==0){
            return 0;
        }
        unordered_set<int> st;
        st.insert(0);
        for(int i=0;i<queries.size();i++){
            auto &q=queries[i];
            if(q[0]<=p&&p<=q[1]){
                vector<int> tmp;
                auto it=st.begin();
                while(it!=st.end()){
                    int a=*it+q[2];
                    if(a==v){
                        return i+1;
                    }else{
                        tmp.push_back(a);
                    }
                    it++;
                }
                for(auto num:tmp){
                    st.insert(num);
                }
            }

        }
        return -1;
    }
};

