        int ans = -1;
        for (int i = 0; i < nums.size(); i++) {
            vector<bool> dp(1001, 0);
            int x = nums[i];
            dp[x] = 1;

            if (x == 0) {
                ans = max(ans, 0);
                continue;
            }
            for (int j = 0; j < queries.size(); j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
