                for (int k = val; k <= x; k++) {
                    if (dp[k]) {
                        dp[k - val] = 1;
                    }
                }
                if (dp[0]) {
                    ans = max(ans, j + 1);
                    break;
                }
            }

            if (!dp[0]) {
                return -1;
            }
        } return ans;
    }
