class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int[][] dp = new int[n][2001];

        for (int[] ints : dp) {
            Arrays.fill(ints, -1);
        }

        int cnt = 0;
        for (int i = 0; i < n; ++ i) {
            dp[i][0] = 1;
            if (nums[i] == 0) {
                cnt ++;
            }
        }

        if (cnt == n) {
            return 0;
        }
        for (int i = 0; i < queries.length; ++ i) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];

            for (int j = l; j <= r; ++ j) {
                int top = nums[j];
                if (dp[j][top] == 1) {
                    continue;
                }

                for (int k = top; k >= val; -- k) {
                    if (dp[j][k - val] > 0) {
                        dp[j][k] = dp[j][k - val];
                    }
                }

                if (dp[j][top] == 1) {
                    cnt ++;
                }
                if (cnt == n) {
                    return i + 1;
                }
            }
        }

        return -1;
    }
}

