class Solution {
     public int minZeroArray(int[] nums, int[][] queries) {
        int ans = 0;
        for (int i = 0; i < nums.length; i++) {
            int computer = computer(queries, nums, i);
            if (computer == -1) return -1;
            ans = Math.max(ans, computer);
        }
        return ans ;
    }

    public int computer(int[][] queries, int[] nums, int j) {
        int n = queries.length;
        boolean[] dp = new boolean[nums[j] + 1];
        dp[0] = true;
        if (dp[nums[j]]) return 0;
        for (int i = 0; i < n; i++) {
            if (queries[i][0] <= j && j <= queries[i][1]) {
                for (int k = nums[j]; k >= queries[i][2]; k--) {
                    dp[k] |= dp[k - queries[i][2]];
                }
            }
            if (dp[nums[j]]) return i+1;
        }
        return -1;
    }
}

