class Solution
{
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries)
    {
        if (all_of(nums.begin(), nums.end(), [](int num)
        {
            return num == 0;
        }))
        {
            return 0;
        }
        int n = nums.size();
        int ans = -1;

        for (int i = 0; i < n; i++)
        {
            int target = nums[i];
            if (target == 0)
            {
                continue;
            }

            vector<int> covers;
            for (int j = 0; j < queries.size(); j++)
            {
                int li = queries[j][0];
                int ri = queries[j][1];
                if (li <= i && i <= ri)
                {
                    covers.push_back(j);
                }
            }

            vector<int> dp(target + 1, INT_MAX);
            dp[0] = -1;

            for (int j: covers)
            {
                int val = queries[j][2];
                for (int s = target; s >= 0; --s)
                {
                    if (dp[s] != INT_MAX)
                    {
                        int new_s = s + val;
                        if (new_s > target) continue;
                        int new_max = max(dp[s], j);
                        if (new_max < dp[new_s])
                        {
                            dp[new_s] = new_max;
                        }
                    }
                }
            }

            if (dp[target] == INT_MAX)
            {
                return -1;
            }
            ans = max(ans, dp[target]);
        }
        return ans == -1 ? 0 : ans + 1;
    }
};

