class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qs) {
        int n = nums.size();

        int ma = 0;
        for (int i = 0; i < n; ++ i){
            int m = nums[i];
            if (m == 0) continue;

            vector<array<int,2>> U;
            for (int j = 0; j < qs.size(); ++ j){
                if (i >= qs[j][0] && i <= qs[j][1]){
                    U.push_back({qs[j][2], j+1});
                }
            }
            vector <int> f(m+1);
            f[0] = 1;
            int cur = 0;
            for (auto &[val, j] : U){
                for (int j = m; j >= val; -- j){
                    if (f[j-val]){
                        f[j] = 1;
                    }
                }
                cur = j;
                if (f[m]) break;
            }
            if (!f[m]) return -1;

            ma = max(ma, cur);
        }

        return ma;
    }
};

