class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int mx = 0;
        for (int x : nums) {
            mx = Math.max(mx, x);
        }
        if (mx == 0) return 0;
        boolean[][] f = new boolean[n][mx + 1];
        for (boolean[] arr : f) {
            arr[0] = true;
        }
        int last = 0;
        int k = 0;
        for (int[] query : queries) {
            k++;
            int l = query[0], r = query[1], v = query[2];
            for (int i = l; i <= r; i++) {
                if (f[i][nums[i]]) continue;
                for (int j = nums[i]; j >= v; j--) {
                    f[i][j] = f[i][j] || f[i][j - v];
                }
            }
            while (last < n && f[last][nums[last]]) {
                last++;
            }
            if (last == n) {
                return k;
            }
        }
        return -1;
    }
}

