class Solution {
public:
    int f[1003];
    int F(int p, int v, vector<vector<int>>& qr) {
        if(!v) return 0;
        memset(f, 0, sizeof f);
        f[0] = 1;
        for(int i = 0; i < qr.size(); ++i) {
            int l = qr[i][0], r = qr[i][1], t = qr[i][2];
            if(l <= p && p <= r) {
                for(int j = v; j >= t; --j)
                    f[j] |= f[j - t];
            }
            if(f[v]) return i + 1;
        } return INT_MAX;
    }
    int minZeroArray(vector<int>& a, vector<vector<int>>& qr) {
        int res = 0;
        for(int i = 0; i < a.size(); ++i) res = max(res, F(i, a[i], qr));
        return res == INT_MAX ? -1 : res;
    }
};

