        ask = [0] * n
        dp = [[False] * (nums[i]+1) for i in range(n)]
        for i in range(n):
            if nums[i] == 0:
                ask[i] = 1
            dp[i][0] = True
        if sum(ask) == n:
            return 0

        for ind, (l, r, val) in enumerate(queries):
            for i in range(l, r+1):
                mx = nums[i]
                if ask[i]:
                    continue
                for j in range(mx, val-1, -1):
                    if dp[i][j-val]:
                        dp[i][j] = True
                if dp[i][mx]:
                    ask[i] = 1
            if sum(ask) == n:
                return ind+1
        return -1

