            for i, (l, r, val) in enumerate(queries):
                if l <= j <= r:
                    cover[j].append((i, val))

        max_k = -1

        for j in range(n):
            target = nums[j]
            if target == 0:
                continue

            dp = {0: -1}
            for (i, val) in cover[j]:
                tmp = {}
                for s in list(dp.keys()):
                    current_max = dp[s]
                    new_s = s + val
                    new_max = max(current_max, i)
                    if new_s in tmp:
                        if new_max < tmp[new_s]:
                            tmp[new_s] = new_max
                    else:
                        tmp[new_s] = new_max

                # tmpdp
                for s in tmp:
                    if s in dp:
                        if tmp[s] < dp[s]:
                            dp[s] = tmp[s]
                    else:
                        dp[s] = tmp[s]

            if target not in dp:
                return -1
            required_max = dp[target]
            if required_max > max_k:
                max_k = required_max

        return max_k + 1 if max_k != -1 else -1

