class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> v(n);
        for (int j = 0; j < m; ++j) {
            int l = queries[j][0];
            int r = queries[j][1];
            for (int i = l; i <= r; ++i) {
                v[i].push_back(j);
            }
        } 
        int max_k = 0;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) continue;
            vector<int> dp(target + 1, INT_MAX);
            dp[0] = -1;   
            for (int j : v[i]) {
                int val = queries[j][2];
                for (int s = target; s >= 0; --s) {
                    if (dp[s] != INT_MAX) {
                        int new_s = s + val;
                        if (new_s > target) continue;
                        int new_max = max(dp[s], j);
                        if (new_max < dp[new_s]) {
                            dp[new_s] = new_max;
                        }
                    }
                }
            }           
            if (dp[target] == INT_MAX) {
                return -1;
            }
            int k_i = dp[target] + 1; 
            max_k = max(max_k, k_i);
        }        
        return max_k;
    }
};

