class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n, m = len(nums), len(queries)

        klist = [0] * n

        for j in range(n):
            if nums[j] == 0:
                klist[j] = 0
                continue

            target = nums[j]
            dp = [False] * (target + 1)
            dp[0] = True
            klist[j] = -1

            for i in range(m):
                l, r, val = queries[i]
                if l <= j <= r:
                    for s in range(target, val - 1, -1):
                        if dp[s - val]:
                            dp[s] = True
                if dp[target]:
                    klist[j] = i + 1
                    break

        if any(val == -1 for val in klist):
            return -1
        return max(klist)

