class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        @cache
        def dfs(idx, num, idy):
            if num == 0:
                return idy
            if idy == n:
                return n+1

            l, r, v = queries[idy]
            res = n+1
            if l <= idx <= r and num >= v:
                res = dfs(idx, num-v, idy+1)

            res = min(res, dfs(idx, num, idy+1))
            return res

        k = 0
        n = len(queries)
        for idx, num in enumerate(nums):
            ans = dfs(idx, num, 0)
            # print(idx, ans)
            if ans == n+1:
                return -1
            k = max(k, ans)
        return k

