                if target == 0:
                    return arr[j - 1][1]

                res = inf
                if target >= arr[j][0]:
                    res = min(res, dfs(j + 1, target - arr[j][0]))
                res = min(res, dfs(j + 1, target))

                return res

            mini = dfs(0, x)
            # print(mini)

            dfs.cache_clear()
            if mini == inf:
                return -1
            min_k = max(mini, min_k)

        return min_k

