class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        if all(x == 0 for x in nums):
            return 0

        q = [[] for _ in range(n)]
        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r + 1):
                if 0 <= j < n:
                    q[j].append((i, val))


        k = -1
        for i in range(n):
            target = nums[i]
            if target == 0:
                continue
            ql = q[i]
            if not ql:
                return -1  
            dp = {0: -1}
            flag = False
            cur = float('inf')
            for (j, val) in ql:
                tmp = {}
                for s in list(dp.keys()):
                    cur_mx = dp[s]
                    n_s = s + val
                    if n_s > target:
                        continue
                    n_mx = max(cur_mx, j)
                    if n_s in tmp:
                        if n_mx < tmp[n_s]:
                            tmp[n_s] = n_mx
                    else:
                        tmp[n_s] = n_mx
                for s in tmp:
                    if s in dp:
                        if tmp[s] < dp[s]:
                            dp[s] = tmp[s]
                    else:
                        dp[s] = tmp[s]
                if target in dp:
                    cur = dp[target]
                    flag = True
                    break
            if not flag and target in dp:
                cur = dp[target]
                flag = True
            if not flag:
                return -1
            if cur > k:
                k = cur

        return k + 1 if k != -1 else -1

