class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), m = q.size();
        vector<map<int, int>> ma(n);
        int cnt = 0;
        vector<int> vis(n);
        for (int i = 0; i < n; i++) {
            ma[i][nums[i]] = 1;
            if (nums[i] == 0) {
                cnt++;
                vis[i] = 1;
            }
        }
        for (int i = 0; i < m; i++) {
            if (cnt == n) {
                return i;
            }
            int l = q[i][0], r = q[i][1], val = q[i][2];
            for (int j = l; j <= r; j++) {
                if (vis[j])
                    continue;
                for (auto [x, y] : ma[j]) {
                    if (x - val < 0)
                        continue;
                    //cout << x - val << 'n';
                    ma[j][x - val] = 1;
                    if (x - val == 0) {
                        cnt++;
                        vis[j] = 1;
                        break;
                    }
                }
            }
        }
        if (cnt == n) {
            return m;
        }
        return -1;
    }
};

