class Solution {
public:
  int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int ret = -1, l = 0, r = queries.size();
    while (l <= r) {
      int mid = (l + r) >> 1;
      if (check(nums, queries, mid)) {
        ret = mid;
        r = mid - 1;
      }
      else {
        l = mid + 1;
      }
    }
    return ret;
  }

  bool canFormSum(int target, vector<int>& nums) {
    vector<bool> dp(target + 1, false);
    dp[0] = true;
    for (int num : nums) {
      for (int j = target; j >= num; j--) {
        if (dp[j - num]) {
          dp[j] = true;
        }
      }
    }
    return dp[target];
  }
  bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
    vector<vector<int>> hou(nums.size() + 1, vector<int>());
    for (int i = 0; i < k; i++) {
      auto q = queries[i];
      for (int i = q[0]; i <= q[1]; i++) {
        hou[i].push_back(q[2]);
      }
    }
    for (int i = 0; i < nums.size(); i++) {
      if (!canFormSum(nums[i], hou[i])) {
        return false;
      }
    }
    return true;
  }
};

