                if (l > k || r < k || v > x) {
                    continue;
                }
                for (int j = 0; j <= x; j++) {
                    if (j + v <= x) {
                        dp[j] = dp[j] || dp[j + v];
                    }
                }
                if (dp[0]) {
                    res = Math.max(res, i + 1);
                    break;
                }
            }
            if (!dp[0]) {
                return -1;
            }
            // System.out.println(res);
        }
        return res;
    }
}

