class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int dp[11][1024] = {0};
        for (int i = 0; i < n; i++) dp[i][0] = 1;

        int matchNum = 0;
        int matchFlag[11] = {0};
        for (int i = 0; i < n; i++) if (nums[i] == 0) matchFlag[i] = 1, matchNum++;
        if (matchNum == n) return 0;
        for (int qid = 0; qid < queries.size(); qid++) {
            auto &q = queries[qid];
            int v = q[2];
            for (int p = q[0]; p <= q[1]; p++) {
                int nv = nums[p];
                if (v > nv) continue;
                if (matchFlag[p]) continue;
                for (int i = nv; i >= v; i--) dp[p][i] |= dp[p][i - v];
                if (dp[p][nv]) matchFlag[p] = 1, matchNum++;
            }
            if (matchNum == n) return qid + 1;
        }
        return -1;
    }
};

