class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int n = nums.size();
        const int m = queries.size();
        vector<int> ans(n, m + 1);
        int res = m + 1;
        for(int i = 0; i < n; i ++ ) {
            const int& num = nums[i];
            bitset<1000 + 1> dp;
            dp.set(num);
            if(dp.test(0)) {
                ans[i] = 0;
                continue;
            }
            for(int j = 0; j < m; j ++ ) {
                const int& l = queries[j][0];
                const int& r = queries[j][1];
                const int& val  = queries[j][2];
                if(i < l || i > r) {
                    continue;
                }
                dp = dp | (dp >> val);
                if(dp.test(0)) {
                    ans[i] = j + 1;
                    break;
                }
            }
        }
        res = *max_element(ans.begin(), ans.end());
        return res == m + 1 ? -1 : res;
    }
};

