class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        a = nums.copy()
        b = [[] for _ in nums]
        for l, r, v in queries:
            for i in range(l, r + 1):
                a[i] -= v
                b[i].append(v)
            for i in chain(range(l), range(r + 1, n)):
                b[i].append(0)
        if any(x > 0 for x in a):
            return -1

        def f(a, v):
            if v == 0:
                return 0
            s = {0}
            for i, x in enumerate(a, 1):
                if x == 0:
                    continue
                b = []
                for y in s:
                    y += x
                    if y == v:
                        return i
                    if y < v:
                        b.append(y)
                s.update(b)
            return len(queries) + 1

        t = max(f(a, v) for a, v in zip(b, nums))
        return t if t <= len(queries) else -1

