import java.util.Arrays;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][]dp = new boolean[n][1001];
        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
        }

        int c = 0;
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                ++c;
            }
        }
        if(c==n)return 0;
        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];

            for(int j=l;j<=r;j++){
                if(dp[j][nums[j]])continue;
                for(int num=dp[j].length-1;num>=v;num--){
                    if(dp[j][num-v]){
                        dp[j][num] = true;
                    }
                }
                if(dp[j][nums[j]])++c;
            }

            if(c==n)return i+1;

        }

        return -1;
    }
}

