            for(int j=0;j<m;++j){
                if(arr[j]>target) continue;
                if(dp[target-arr[j]]){
                    dp[target] = 1;
                    ans = max(ans,idx[i][j]);break;
                }
                for(int k=target-1;k>=arr[j];--k){
                    if(dp[k-arr[j]]) dp[k] = 1;
                }
            }
            if(!dp[target]) return -1;
        }
        return ans+1;
    }
};

