class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ret = -1;
        for(int i = 0; i < nums.size(); i++){
            if(nums[i] == 0){
                ret = max(ret, 0);
                continue;
            }
            int j = 0;
            unordered_map<int, int> hash;
            hash[nums[i]] = 1;
            for( ; j < queries.size(); j++){
                int left = queries[j][0],
                    right = queries[j][1],
                    val = queries[j][2];
                if(i < left || i > right) continue;
                auto hash_tmp = hash;
                for(auto& [e, v] : hash){
                    if(e - val == 0){
                        nums[i] = 0;
                        j++;
                        // cout << "out " << e << endl;
                        break;
                    }
                    hash_tmp[e - val]++;
                }
                hash = hash_tmp;
                // cout << "j = " << j << endl;
                // for(auto [e, v] : hash) cout << e << ' ' << v << endl;
                if(nums[i] == 0) break;
            }
            // cout << "out j = " << j << endl;
            if(nums[i]) return -1;
            ret = max(ret, j);
        }
        return ret;
    }
};

