class Solution {

    private int judge(int[] nums, int total) {
        if (total == 0) {
            return 0;
        }
        boolean[] dp = new boolean[total + 1];
        dp[0] = true;
        for (int i = 0;i < nums.length;i++) {
            int x = nums[i];
            if (x == -1) {
                continue;
            }
            for (int j = dp.length - 1;j >= 0;j--) {
                if (!dp[j]) {
                    continue;
                }
                if (j + x >= dp.length) {
                    continue;
                }
                dp[j + x] = true;
            }
            if (dp[dp.length - 1]) {
                return i + 1;
            }
        }
        return -1;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int ret = 0;
        for (int i = 0;i < nums.length;i++) {
            int x = nums[i];
            int[] data = new int[queries.length];
            for (int j = 0;j < queries.length;j++) {
                int[] query = queries[j];
                if (query[0] <= i && i <= query[1]) {
                    data[j] = query[2];
                } else {
                    data[j] = -1;
                }
            }
            int temp = judge(data, x);
            if (temp == -1) {
                return -1;
            }
            ret = Math.max(ret, temp);
        }
        return ret;
    }

}

