class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def check(cnt2: int) -> bool:
            cnt = [[] for _ in range(10)]
            for i in range(cnt2):
                l, r, val = queries[i]
                for j in range(l, r + 1):
                    cnt[j].append(val)
            for row in cnt:
                row.sort()
                # f[i][j] represent first i-th val with j remain
            for length in range(len(nums)):
                cap = nums[length]
                f = [[False] * (cap + 1) for _ in range(len(cnt[length]) + 1)]
                for a in range(len(cnt[length]) + 1):
                    f[a][0] = True
                for a in range(len(cnt[length])):
                    for b in range(cap + 1):
                        f[a + 1][b] = f[a][b]
                        if b >= cnt[length][a]:
                            f[a + 1][b] |= f[a][b - cnt[length][a]]
                if not f[-1][-1]: return False
            return True
        q = len(queries)
        left, right = 0, q
        ans = -1
        while left <= right:
            mid = (left + right) // 2
            if check(mid):
                ans = mid
                right = mid - 1
            else:
                left = mid + 1
        return ans

