class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& que) {
        int n = nums.size(),m = que.size();

        auto check = [&](int i)->int{
            int l=0,r=m;
            while(l<=r){
                int mid = (l+r)/2;
                vector<vector<int>> f(mid+1,vector<int>(nums[i]+1));
                f[0][0] = 1;
                for(int j=1;j<=mid;++j){
                    int jl = que[j-1][0],jr = que[j-1][1], jval = que[j-1][2];
                    for(int v=0;v<=nums[i];++v){
                        f[j][v] = f[j-1][v];
                        if(i>=jl && i<=jr && v>=jval){
                            f[j][v] = max(f[j][v],f[j-1][v-jval]);
                        }
                    }
                }
                if(f[mid][nums[i]]) r = mid - 1;
                else l = mid + 1;
            }
            return l;
        };

        int ans = 0;
        for(int i=0;i<n;++i){
           ans = max(ans,check(i));
        }
        return ans == m+1 ? -1 : ans;
    }
};

