class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
    int q = queries.size();
    vector<vector<pair<int, int>>> cover_queries(n); // (val)

    // 
    for (int j = 0; j < q; ++j) {
        int l = queries[j][0];
        int r = queries[j][1];
        int val = queries[j][2];
        // 
        for (int i = l; i <= r; ++i) {
            if (i >= 0 && i < n) {
                cover_queries[i].emplace_back(j, val);
            }
        }
    }

    int max_x = -1;
    for (int i = 0; i < n; ++i) {
        int target = nums[i];
        if (target == 0) {
            continue; // max_x
        }

        auto& cq = cover_queries[i];
        long long sum_val = 0;
        for (auto& p : cq) {
            sum_val += p.second;
        }
        if (sum_val < target) {
            return -1;
        }

        unordered_map<int, int> dp;
        dp[0] = -1;
        int x_i = INT_MAX;
        bool found = false;

        for (auto& p : cq) {
            int j = p.first;
            int val = p.second;

            unordered_map<int, int> tmp;
            // 
            for (auto& entry : dp) {
                int s = entry.first;
                int current_max = entry.second;
                if (tmp.find(s) == tmp.end() || current_max < tmp[s]) {
                    tmp[s] = current_max;
                }
            }

            // 
            for (auto& entry : dp) {
                int s = entry.first;
                int current_max = entry.second;
                int new_s = s + val;
                if (new_s > target) continue;
                int new_max = max(current_max, j);
                if (tmp.find(new_s) == tmp.end() || new_max < tmp[new_s]) {
                    tmp[new_s] = new_max;
                }
            }

            dp.swap(tmp);

            if (dp.count(target)) {
                if (dp[target] < x_i) {
                    x_i = dp[target];
                    found = true;
                    break; // x_i
                }
            }
        }

        if (!found) {
            if (dp.count(target)) {
                x_i = dp[target];
            } else {
                return -1;
            }
        }

        max_x = max(max_x, x_i);
    }

    // 0
    if (max_x == -1) {
        for (int num : nums) {
            if (num != 0) {
                return -1;
            }
        }
        return 0;
    }

    return max_x + 1;
    }
};

