bool canAchieveTarget(int target, int *coins, int coinsCount) {
    bool *dp = (bool*)calloc(target + 1, sizeof(bool));
    dp[0] = true;
    for (int i = 0; i < coinsCount; i++) {
        int coin = coins[i];
        for (int s = target; s >= coin; s--) {
            if (dp[s - coin])
                dp[s] = true;
        }
    }
    bool res = dp[target];
    free(dp);
    return res;
}

bool isFeasible(int k, int *nums, int n, int **queries) {
    for (int i = 0; i < n; i++) {
        int cnt = 0;
        for (int j = 0; j < k; j++) {
            int l = queries[j][0], r = queries[j][1];
            if (i >= l && i <= r)
                cnt++;
        }
        int *coins = (int*)malloc(cnt * sizeof(int));
        int index = 0;
        for (int j = 0; j < k; j++) {
            int l = queries[j][0], r = queries[j][1];
            if (i >= l && i <= r) {
                coins[index++] = queries[j][2];
            }
        }
        if (nums[i] != 0) {
            if (!canAchieveTarget(nums[i], coins, cnt)) {
                free(coins);
                return false;
            }
        }
        free(coins);
    }
    return true;
}

int minZeroArray(int* nums, int numsSize, int** queries, int queriesSize, int* queriesColSize) {
    if (numsSize == 0)
        return 0;
    if (isFeasible(0, nums, numsSize, queries))
        return 0;
    for (int k = 1; k <= queriesSize; k++) {
        if (isFeasible(k, nums, numsSize, queries))
            return k;
    }
    return -1;
}

