class Solution {
public:
    int minDo(int num, vector<vector<int>>& ddo) {
        if (num == 0) return 0;
        set<int> s;
        s.insert(num);
        for (int i = 0; i < ddo.size(); i++) {
            set<int> ns;
            for (int t : s) {
                if (t == ddo[i][0]) return ddo[i][1] + 1;
                if (t - ddo[i][0] > 0) ns.insert(t - ddo[i][0]);
            }
            for (int t : ns) s.insert(t);
        }
        return -1;

    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<vector<int>>> vv;
        for (int i = 0; i < nums.size(); i++) vv.push_back(vector<vector<int>>());
        for (int i = 0; i < queries.size(); i++) {
            auto &q = queries[i];
            for (int j = q[0]; j <= q[1]; j++) {
                vv[j].push_back({q[2], i});
            }
        }
        int maxDo = -1;
        for (int i = 0; i < nums.size(); i++) {
            int md = minDo(nums[i], vv[i]);
            if (md == -1) return -1;
            maxDo = max(maxDo, md);
        }
        return maxDo;
    }
};

