class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<unordered_set<int>> vals(n);
        vector<int> oks(n, 0);
        for (int i = 0; i < n; i++)
            if (nums[i] == 0)
                oks[i] = 1;
        bool check = true;
        for (int i = 0; i < n; i++) {
            if (oks[i] == 0) {
                check = false;
                break;
            }
        }
        if (check)
            return 0;
        for (int i = 0; i < m; i++) {
            int l = queries[i][0], r = queries[i][1], v = queries[i][2];
            for (int j = l; j <= r; j++) {
                if (oks[j] || v > nums[j])
                    continue;
                if (v == nums[j]) {
                    oks[j] = 1;
                    continue;
                }
                int nv = vals[j].size();
                vector<int> tmp = {v};
                for (auto x: vals[j]) {
                    if (v + x < nums[j]) {
                        tmp.push_back(v + x);   
                    } else if (v + x == nums[j]) {
                        oks[j] = 1;
                        break;
                    }
                }
                vals[j].insert(tmp.begin(), tmp.end());
            }
            bool found = true;
            for (int j = 0; j < n; j++) {
                if (!oks[j]) {
                    found = false;
                    break;
                }
            }
            if (found)
                return i + 1;
        }
        return -1;
    }
};

