class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // sort(queries.begin(), queries.end());
        int ln = nums.size();
        int lq = queries.size();
        int min_q = 0;
        for (int i = 0; i < ln; ++i) {
            // cout << nums[i] << ":" << endl;
            if (nums[i] == 0) {
                continue;
            }
            vector<int> hash(nums[i] + 1, 0);
            hash[0] = 1;
            for (int q = 0; q < lq; ++q) {
                if (queries[q][1] < i || queries[q][0] > i) {
                    continue;
                }
                // ----------------------------------
                int v = queries[q][2];
                // cout << q << ":" << v << ":" << endl;
                if (v > nums[i]) {
                    continue;
                }
                for (int idx = nums[i] - v; idx >= 0; --idx) {
                    if (hash[idx] == 1) {
                        hash[idx + v] = 1;
                    }
                }
                // for (int idx = 0; idx <= nums[i]; ++idx) {
                //     cout << hash[idx] << " ";
                // }
                // cout << endl;
                min_q = max(min_q, q + 1);
                if (hash[nums[i]] == 1) {
                    break;
                }
            }
            if (hash[nums[i]] != 1) {
                return -1;
            }
        }
        return min_q;
    }
};

