func minZeroArray(nums []int, queries [][]int) (ans int) {
    n := len(nums)

    for i := 0; i < n; i++ {
        if nums[i] == 0 {
            continue
        }

        a := []int{}
        for q := 0; q < len(queries); q++ {
            li, ri := queries[q][0], queries[q][1]
            if li <= i && i <= ri {
                a = append(a, q)
            }
        }

        if len(a) == 0 {
            return -1
        }

        vals := make([]int, len(a))
        for j, q := range a {
            vals[j] = queries[q][2]
        }

        target := nums[i]
        f := make([]bool, target+1)
        f[0] = true
        minT := -1

        for t := 0; t < len(vals); t++ {
            val := vals[t]
            for j := target; j >= val; j-- {
                if f[j-val] {
                    f[j] = true
                    if j == target && minT == -1 {
                        minT = t
                    }
                }
            }
        }

        if !f[target] {
            return -1
        }

        ans = max(ans, a[minT] + 1)
    }

    return ans
}

