class Solution {
  public int minZeroArray(int[] nums, int[][] queries) {
    boolean allZero = true;
    for (int v : nums) {
      if (v != 0) {
        allZero = false;
        break;
      }
    }
    if (allZero) {
      return 0;
    }
    int len = nums.length, qLen = queries.length, limit = 1 << 10;
    boolean[][] bits = new boolean[len][limit];
    for (int i = 0; i < qLen; i++) {
      int l = queries[i][0], r = queries[i][1], v = queries[i][2];
      for (int t = l; t <= r; t++) {
        int nv = nums[t];
        if (bits[t][nv]) {
          continue;
        }
        for (int x = limit - 1; x >= v; x--) {
          int bx = x - v;
          if (bits[t][bx]) {
            bits[t][x] = true;
          }
        }
        bits[t][v] = true;
      }
      boolean valid = true;
      for (int t = 0; t < len; t++) {
        if (nums[t] > 0 && !bits[t][nums[t]]) {
          valid = false;
          break;
        }
      }
      if (valid) {
        return i + 1;
      }
    }
    return -1;
  }
}

