class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<int[]>[] g = new List[n];
        for (int i = 0; i < n; i++) {
            g[i] = new ArrayList<>();
        }
        for (int k = 0; k < queries.length; k++) {
            int[] q = queries[k];
            int l = q[0], r = q[1], val = q[2];
            for (int i = l; i <= r; i++) {
                g[i].add(new int[]{val, k});
            }
        }
        int res = -1;
        for (int i = 0; i < n; i++) {
            int cur = solve(g[i], nums[i]);
            if (cur == -1) {
                return -1;
            }
            res = Math.max(res, cur);
        }
        return res;
    }

    private int solve(List<int[]> list, int x) {
        if (x == 0) {
            return 0;
        }
        boolean[] dp = new boolean[x + 1];
        dp[0] = true;
        for (int[] item : list) {
            int y = item[0];
            for (int i = x; i >= y; i--) {
                dp[i] |= dp[i - y];
                if (dp[x]) {
                    return item[1] + 1;
                }
            }
        }
        return -1;
    }
}

