class Solution {
public:
int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    int m = queries.size();
    auto check = [&](int mid) -> bool {
        // cerr << "mid = " << mid << endl;
        if (mid == m + 1)
            return true;
        vector<vector<int>> add(n);
        for (int i = 0; i < mid; i++) {
            auto& q = queries[i];
            for (int i = q[0]; i <= q[1]; i++) {
                add[i].push_back(q[2]);
            }
        }
        // for (auto& v : add) {
        //     for (int i : v) {
        //         cerr << i << " ";
        //     }
        //     cerr << endl;
        // }
        for (int k = 0; k < n; k++) {
            vector dp(nums[k] + 1, 0);
            dp[0] = 1;
            for (int i : add[k]) {
                for (int j = nums[k]; j >= i; j--) {
                    dp[j] |= dp[j - i];
                }
            }
            // for (int i = 0; i <= nums[k]; i++) {
            //     cerr << dp[i] << " ";
            // }
            // cerr << endl;
            if (!dp[nums[k]])
                return false;
        }
        return true;
    };
    int L = 0, R = m + 1;
    while (L < R) {
        int mid = (L + R) >> 1;
        if (check(mid))
            R = mid;
        else
            L = mid + 1;
    }
    if (L == m + 1)
        return -1;
    return L;
}
};

