        // if(i == 2) cout << v << " " << i << " " << j << endl;
        if(m[v][j] < 1e4) {
            return m[v][j];
        }
        if(i < q[j][0] || i > q[j][1]) {
            m[v][j] = dfs(v, i, j + 1);
            return m[v][j];
        }
        if(v == q[j][2]) {
            res = min(res, j);
            m[v][j] = j;
            return j;
        }
        // cout << v << " " << i << " " << j << endl;
        int c = dfs(v, i, j + 1);
        if(v > q[j][2]) {
            c = min(c, dfs(v - q[j][2], i, j + 1));
        }
        m[v][j] = c;
        return c;
    }

    int res;
    // unordered_map<int, unordered_map<int, int>> m;
    vector<vector<int>> q;

    int minZeroArray(vector<int>& a, vector<vector<int>>& q_) {
        if(*max_element(a.begin(), a.end()) == 0) {
            return 0;
        }
        q = q_;
        int n = a.size();
        vector<int> k(n);
        for(int i = 0; i < n; i++) {
            if(a[i] == 0) {
                continue;
            }
            memset(m, 127, (a[i] + 1) * 1005 * 4);
            // m.clear();
            res = q.size() + 1;
            int c = dfs(a[i], i, 0);
            // cout << i << " " << res << endl;
            if(c >= q.size()) {
                return -1;
            }
            k[i] = c;
        }
        res = 0;
        for(auto &i : k) {
            res = max(res, i);
        }
        return res + 1;
    }
};

