/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function (nums, queries) {
  const n = nums.length
  const check = (m) => {
    for (let i = 0; i < n; i++) {
      const sum = nums[i]
      const f = new Array(sum + 1).fill(false)
      f[0] = true
      for (let j = 0; j <= m; j++) {
        const [l, r, v] = queries[j]
        // 
        for (let s = sum; s >= 0; s--) {
          if (i >= l && i <= r && s >= v) {
            f[s] = f[s] || f[s - v]
          }
        }
      }
      if (!f[sum]) return false
    }
    return true
  }

  if (_.sum(nums) === 0) {
    return 0
  }

  let l = 0, r = queries.length;
  // 
  while (l < r) {
    const mid = Math.floor((l + r) / 2)
    if (check(mid)) {
      r = mid
    } else {
      l = mid + 1
    }
  }
  return l === queries.length ? -1 : l + 1
};

