class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    int m = queries.size();
    vector<vector<pair<int, int>>> cover(n);
    for (int i = 0; i < m; i++) 
    {
        int l = queries[i][0];
        int r = queries[i][1];
        int val = queries[i][2];
        for (int x = l; x <= r; x++) 
            if (x >= 0 && x < n) cover[x].emplace_back(i, val);
    }

    vector<int> allmax;
    for (int i = 0; i < n; i++) 
    {
        int num = nums[i];
        if (num == 0) 
        {
            allmax.emplace_back(-1);
            continue;
        }

        auto& que = cover[i];
        unordered_set<int> p = {0};
        bool found = false;
        int jmax = -1;
        for (auto& [idx, val] : que) {
            unordered_set<int> newp(p);
            for (auto s : p) newp.insert(s + val);
            p.swap(newp);
            if (p.count(num)) 
            {
                jmax = idx;
                found = true;
                break;
            }
        }
        if (!found) return -1;
        allmax.emplace_back(jmax);
    }
    int maxj = -1;
    bool flag = false;
    for (auto j : allmax) 
    {
        if (j != -1) 
        {
            flag=true;
            if(j>maxj) maxj=j;
        }
    }

    if (!flag) return 0;
    return maxj + 1;
    }
};

